(4, 1, '2013-09-09 19:02:57', '2013-09-09 19:02:57', '<span style="color: #ff99cc;">Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!</span>\n\n&nbsp;', 'Hello world!', '', 'inherit', 'open', 'open', '', '1-autosave-v1', '', '', '2013-09-09 19:02:57', '2013-09-09 19:02:57', '', 1, 'http://localhost:8888/web3/wptestsite/?p=4', 0, 'revision', '', 0),
(5, 1, '2013-09-09 19:03:36', '2013-09-09 19:03:36', '<span style="color: #ff99cc;">Welcome to WordPress. This is your first post. Edit or delete it, then start blogging!</span>\r\n\r\n&nbsp;', 'Hello world!', '', 'inherit', 'open', 'open', '', '1-revision-v1', '', '', '2013-09-09 19:03:36', '2013-09-09 19:03:36', '', 1, 'http://localhost:8888/web3/wptestsite/?p=5', 0, 'revision', '', 0),
